INSERT INTO drug (name, count, producer, price, description) VALUES ('Paralen 500', 36, 1, 10.99, 'Paracetamol, léčivá látka přípravku Paralen 500 tablety, působí proti bolesti a snižuje zvýšenou tělesnou teplotu.');
INSERT INTO drug (name, count, producer, price, description) VALUES ('Ibalgin 400', 59, 1, 13.99, 'Ibalgin 400 zmírňuje bolest a zánět různého původu a zároveň tlumí horečku provázející nemoci z nachlazení u dospělých a dospívajících od 12 let.');
INSERT INTO drug (name, count, producer, price, description) VALUES ('Endiaron', 20, 1, 5.99, 'Endiaron tablety jsou střevní dezinficiens používané k léčbě infekčních průjmů u dospělých, dospívajících a dětí o tělesné hmotnosti vyšší než 40 kg.');
INSERT INTO drug (name, count, producer, price, description) VALUES ('Laktobacily 6', 15, 1, 12.99, 'Komplex laktobacilů a bifidobakterií: 6 kmenů, 5 miliard mikroorganismů.');
INSERT INTO drug (name, count, producer, price, description) VALUES ('Stoptussin sirup 180 ml', 78, 1, 5.99, 'Přípravek tlumí dráždění ke kašli a mění skladbu průduškového hlenu a usnadňuje tak jeho vykašlávání. Sirup k léčbě suchého, dráždivého a obtížně ztišitelného kašle.');
INSERT INTO drug (name, count, producer, price, description) VALUES ('Olfen gel 100 g', 35, 1, 8.99, 'K léčbě bolestí spojených s akutním natažením nebo pohmožděním.');
INSERT INTO drug (name, count, producer, price, description) VALUES ('Orlistat Sandoz 60 mg', 24, 1, 16.99, 'Přípravek se používá ke snížení tělesné hmotnosti u dospělých od 18 let s nadváhou, kteří mají index BMI rovný nebo vyšší než 28. Přípravek Orlistat Sandoz 60 mg se má užívat společně s dietou se sníženým obsahem kalorií a nízkým obsahem tuků.');
INSERT INTO drug (name, count, producer, price, description) VALUES ('Boiron Oscillococcinum', 43, 1, 20.99, 'Homeopatický léčivý přípravek.');
INSERT INTO drug (name, count, producer, price, description) VALUES ('Glyvenol 400', 43, 1, 15.99, 'Glyvenol 400 se používá k léčbě onemocnění žilního krevního oběhu a u hemoroidů u dospělých a dospívajících od 12 let.');
INSERT INTO drug (name, count, producer, price, description) VALUES ('Stoptussin sirup 180 ml', 54, 1, 5.99, 'Přípravek tlumí dráždění ke kašli a mění skladbu průduškového hlenu a usnadňuje tak jeho vykašlávání. Sirup k léčbě suchého, dráždivého a obtížně ztišitelného kašle.');
INSERT INTO drug (name, count, producer, price, description) VALUES ('ACC LONG 600 mg 20', 10, 1, 7.99, 'ACC LONG usnadňuje vykašlávání. Užívá se k léčbě vlhkého kašle. Přípravek mohou užívat dospělí a děti starší než 14 let.');
INSERT INTO drug (name, count, producer, price, description) VALUES ('Visine Classic oční kapky 15 ml', 12, 1, 4.99, 'Spolehlivě odstraní zarudnutí očí. Kapky jsou vhodné také k léčbě alergických obtíží.');
INSERT INTO drug (name, count, producer, price, description) VALUES ('Vidisic Oční gel 3x10 g', 15, 1, 8.99, 'Přípravek se používá jako náhrada slzní tekutiny při nedostatečné produkci slz, při léčbě příznaků suchého oka (suchý zánět spojivek) a jako ochrana proti dalšímu podráždění.');
INSERT INTO drug (name, count, producer, price, description) VALUES ('Ophthalmo-Septonex oční mast 5 g', 3, 1, 3.99, 'Ophthalmo-Septonex oční mast se používá při léčbě nehnisavých zánětů spojivek a očních víček, při poškození rohovky cizím tělískem u dospělých i dětí.');
INSERT INTO drug (name, count, producer, price, description) VALUES ('Prostamol uno 320 mg', 6, 1, 22.99, 'Prostamol Uno obsahuje skupinu látek, které působí různými mechanismy na zbytnělou prostatu a zlepšují močové obtíže. Přípravky s extraktem z plodů Serenoa repens nenarušují sexuální funkce.');
INSERT INTO drug (name, count, producer, price, description) VALUES ('Prostakan Forte', 68, 1, 21.99, 'Prostakan forte se používá při benigní hyperplazii (nezhoubném zbytnění) prostaty, především její počáteční stadia (stadium I - II). Jsou nutné pravidelné kontroly lékařem - urologem.');
INSERT INTO drug (name, count, producer, price, description) VALUES ('Fenistil kapky 20 ml', 54, 1, 5.99, 'Fenistil kapky se používají k léčbě příznaků alergie (kožní vyrážky, svědění, senná rýma, jiné druhy alergické rýmy) u dospělých, dospívajících a dětí od 1 roku.');
INSERT INTO drug (name, count, producer, price, description) VALUES ('Analergin 10 mg', 54, 1, 7.99, 'Analergin se používá k zmírnění nosních a očních příznaků sezónní a celoroční alergické rýmy a ke zmírnění příznaků chronické kopřivky nejasného původu.');
INSERT INTO drug (name, count, producer, price, description) VALUES ('Allergodil nosní sprej 10 ml', 75, 1, 9.99, 'Allergodil nosní sprej se používá k léčbě nosních příznaků sezónní alergické rýmy (senné rýmy) a celoroční alergické rýmy.');
INSERT INTO drug (name, count, producer, price, description) VALUES ('Hylak forte roztok 100 ml', 47, 1, 8.99, 'Hylak Forte se užívá při nadýmání, průjmu nebo zácpě, poruchách trávení, při trávicích obtížích u dospělých i dětí včetně kojenců.');
INSERT INTO drug (name, count, producer, price, description) VALUES ('Pangrol 20000', 25, 1, 9.99, 'Pangrol 20 000 se používá u chorob slinivky břišní, provázených poruchou její zevní sekrece (např. při zánětech slinivky břišní, mukoviscidóze), po operacích žaludku a tenkého střeva (resekcích) a při poruchách vylučování žlučí.');
INSERT INTO drug (name, count, producer, price, description) VALUES ('Rennie 96 žvýkacích tablet', 1, 1, 7.99, 'Rychlá a bezpečná* úleva od pálení žáhy. Při pálení žáhy, při překyselení žaludku a pocitu plnosti.');
INSERT INTO drug (name, count, producer, price, description) VALUES ('Apo-Ome 14 tablet', 15, 1, 6.99, 'APO-OME 20 se užívá na úlevu od příznaků pálení žáhy a regurgitace (návrat kyselého žaludečního obsahu zpět do jícnu).');
INSERT INTO drug (name, count, producer, price, description) VALUES ('Famosan 10 mg', 78, 1, 2.99, 'Famosan 10 mg tablety se užívají ke krátkodobé léčbě žaludečních obtíží (příznaky žaludečního překyselení, pálení žáhy a nevolnosti) u dospělých.');
INSERT INTO drug (name, count, producer, price, description) VALUES ('Persen 40 obalených table', 15, 1, 7.99, 'Léčivý přípravek s tradiční kombinací bylin se zklidňujícím účinkem (kozlík, meduňka, máta).');
